#include <iostream>
#include <vector>
#include <string>
#include <sstream>

void trymove(std::vector<std::vector<char>>& map, int x, int y){
    int posx, posy;
    for(int i = 0; i < map.size(); i++){
        for (int j = 0; j < map[0].size(); j++){
            if (map[i][j] == '@') {
                posx = i;
                posy = j;
            }
        }
    }
    if (map[posx + x][posy + y] == '#') return;
    if (map[posx + x][posy + y] == '.') {
        map[posx + x][posy + y] = '@';
        map[posx][posy] = '.';
        return;
    }
    int currx = posx + x, curry = posy + y;
    while (true){
        if(map[currx][curry] == '.'){
            map[currx][curry] = 'O';
            map[posx + x][posy + y] = '@';
            map[posx][posy] = '.';
            return;
        }
        if (map[currx][curry] == '#') return;
        currx += x;
        curry += y;
    }
}

